import { FormMessage } from '@/registry/default/blocks/password-based-auth-react-router/components/form-message'
import { createClient } from '@/registry/default/clients/react-router/lib/supabase/server'
import { Button } from '@/registry/default/components/ui/button'
import { Input } from '@/registry/default/components/ui/input'
import { Label } from '@/registry/default/components/ui/label'
import type { ActionFunctionArgs } from 'react-router'
import { Form, Link, redirect, useActionData, useNavigation } from 'react-router'

export const action = async ({ request }: ActionFunctionArgs) => {
  const { supabase, headers } = createClient(request)
  const formData = await request.formData()

  const { error } = await supabase.auth.signInWithPassword({
    email: formData.get('email') as string,
    password: formData.get('password') as string,
  })

  if (error) {
    return { success: false, error: error.message }
  }

  // Redirect to home page after successful sign-in
  return redirect('/', { headers })
}

const SignIn = () => {
  const actionData = useActionData<typeof action>()
  const navigation = useNavigation()
  const isSubmitting = navigation.state === 'submitting'

  return (
    <div className="max-w-md mx-auto mt-24">
      <Form method="post" className="flex-1 flex flex-col min-w-64">
        <h1 className="text-2xl font-medium">Sign in</h1>
        <p className="text-sm text-foreground">
          Don&apos;t have an account?{' '}
          <Link className="text-foreground font-medium underline" to="/sign-up">
            Sign up
          </Link>
        </p>
        <div className="flex flex-col gap-2 [&>input]:mb-3 mt-8">
          <Label htmlFor="email">Email</Label>
          <Input name="email" placeholder="you@example.com" required />
          <div className="flex justify-between items-center">
            <Label htmlFor="password">Password</Label>
            <Link className="text-xs text-foreground underline" to="/forgot-password">
              Forgot Password?
            </Link>
          </div>
          <Input type="password" name="password" placeholder="Your password" required />
          <Button disabled={isSubmitting}>{isSubmitting ? 'Signing in...' : 'Sign in'}</Button>

          {actionData?.error && <FormMessage message={actionData.error} />}
        </div>
      </Form>
    </div>
  )
}

export default SignIn

import { FormMessage } from '@/registry/default/blocks/password-based-auth-react-router/components/form-message'
import { createClient } from '@/registry/default/clients/react-router/lib/supabase/server'
import { Button } from '@/registry/default/components/ui/button'
import { Input } from '@/registry/default/components/ui/input'
import { Label } from '@/registry/default/components/ui/label'
import type { ActionFunctionArgs } from 'react-router'
import { Form, Link, useActionData, useNavigation } from 'react-router'

export const action = async ({ request }: ActionFunctionArgs) => {
  const { supabase } = createClient(request)
  const formData = await request.formData()

  const { error } = await supabase.auth.signUp({
    email: formData.get('email') as string,
    password: formData.get('password') as string,
  })

  if (error) {
    return { success: false, error: error.message }
  }

  return {
    success: true,
    error: null,
    message: 'Success! Please check your email for further instructions.',
  }
}

export const SignUp = () => {
  const actionData = useActionData<typeof action>()
  const navigation = useNavigation()
  const isSubmitting = navigation.state === 'submitting'

  return (
    <div className="max-w-md mx-auto mt-24">
      <Form method="post" className="flex-1 flex flex-col min-w-64">
        <h1 className="text-2xl font-medium">Sign up</h1>
        <p className="text-sm text-foreground">
          Already have an account?{' '}
          <Link className="text-foreground font-medium underline" to="/sign-in">
            Sign in
          </Link>
        </p>
        <div className="flex flex-col gap-2 [&>input]:mb-3 mt-8">
          <Label htmlFor="email">Email</Label>
          <Input name="email" placeholder="you@example.com" required />
          <div className="flex justify-between items-center">
            <Label htmlFor="password">Password</Label>
          </div>
          <Input type="password" name="password" placeholder="Your password" required />
          <Button disabled={isSubmitting}>{isSubmitting ? 'Signing up...' : 'Sign up'}</Button>

          {!actionData?.success && actionData?.error && <FormMessage message={actionData.error} />}
          {actionData?.success && (
            <FormMessage message={'Success! check your email for further instructions.'} />
          )}
        </div>
      </Form>
    </div>
  )
}

export default SignUp

import { createClient } from '@/registry/default/clients/react-router/lib/supabase/server'
import type { ActionFunctionArgs } from 'react-router'
import { redirect } from 'react-router'

export async function action({ request }: ActionFunctionArgs) {
  const { supabase, headers } = createClient(request)

  const { error } = await supabase.auth.signOut()

  if (error) {
    console.error(error)
    return { success: false, error: error.message }
  }

  // Redirect to dashboard or home page after successful sign-in
  return redirect('/', { headers })
}

import { createClient } from '@/registry/default/clients/react-router/lib/supabase/server'
import { Button } from '@/registry/default/components/ui/button'
import { Input } from '@/registry/default/components/ui/input'
import { AuthApiError } from '@supabase/supabase-js'
import { type ActionFunctionArgs, Form, data, useActionData } from 'react-router'

export const action = async ({ request }: ActionFunctionArgs) => {
  const formData = await request.formData()
  const email = formData.get('email') as string

  if (!email) {
    return data({
      message: 'Please provide an email',
      errors: { email: 'Email is required' },
      data: { email: '' },
    })
  }

  const { supabase, headers } = createClient(request)
  const origin = new URL(request.url).origin

  // Send the actual reset password email
  const { error } = await supabase.auth.resetPasswordForEmail(email, {
    redirectTo: `${origin}/auth/confirm?next=/update-password`,
  })

  if (error) {
    if (error instanceof AuthApiError && error.status === 400) {
      return data(
        {
          message: 'Invalid credentials.',
          errors: { email: 'Invalid email address' },
          data: { email },
        },
        { headers }
      )
    }
    return data(
      {
        message: error.message,
        errors: { email: error.message },
        data: { email },
      },
      { headers }
    )
  }

  return data(
    {
      message: 'Please check your email for a password reset link to log into the website.',
      errors: {},
      data: { email: '' },
    },
    { headers }
  )
}

export default function ForgotPassword() {
  const actionData = useActionData<typeof action>()

  return (
    <div className="max-w-md mx-auto mt-24">
      {actionData?.message ? (
        <div className={`alert-error mb-10`}>{actionData?.message}</div>
      ) : null}
      <h2 className="font-semibold text-xl mb-4">Forgot Password</h2>
      <p className="mb-4">Reset your password using your email address</p>
      <Form method="post">
        <div className="form-control">
          <Input
            id="email"
            name="email"
            type="text"
            placeholder="you@example.com"
            defaultValue={actionData?.data?.email}
            className="input input-bordered"
          />
        </div>
        {actionData?.errors &&
          Object.values(actionData.errors).map((error, index) =>
            error ? (
              <div key={index} className="text-error mt-1">
                {error as string}
              </div>
            ) : null
          )}
        <div className="form-control mt-6">
          <Button className="btn btn-primary no-animation">Send</Button>
        </div>
      </Form>
    </div>
  )
}

import type { ActionFunctionArgs } from 'react-router'
import { Form, redirect, useActionData } from 'react-router'

import { createClient } from '@/registry/default/clients/react-router/lib/supabase/server'
import { Button } from '@/registry/default/components/ui/button'
import { Input } from '@/registry/default/components/ui/input'

export const action = async ({ request }: ActionFunctionArgs) => {
  const { supabase, headers } = createClient(request)
  const formData = await request.formData()
  const password = formData.get('password') as string

  const { error } = await supabase.auth.updateUser({
    password: password,
  })

  if (error) {
    return { success: false, error: error.message }
  }

  // Redirect to sign-in page after successful password update
  return redirect('/sign-in', { headers })
}

const ForgotPassword = () => {
  const actionData = useActionData<typeof action>()

  return (
    <div className="max-w-md mx-auto mt-24">
      <p>Update your password</p>
      <Form method="post" className="grid gap-4 mt-4">
        <Input type="password" name="password" placeholder="Password" required />
        <br />
        <Button type="submit">Update password</Button>
      </Form>
      {actionData?.error && <p style={{ color: 'red' }}>{actionData.error}</p>}
    </div>
  )
}

export default ForgotPassword

type FormMessageProps = {
  message: string | null
}

export function FormMessage({ message }: FormMessageProps) {
  return (
    <div className="flex flex-col gap-2 w-full max-w-md text-sm">
      {message && (
        <div className="text-foreground border-l-2 border-foreground px-4">{message}</div>
      )}
    </div>
  )
}

