        <DrawerContent className="h-[90vh] p-0 overflow-hidden">
          <DrawerHeader className="sr-only">
            <DrawerTitle>Manage your back-end</DrawerTitle>
          </DrawerHeader>
          {content}
        </DrawerContent>
      </Drawer>
    </QueryClientProvider>
  )
}

