// Define log types with names and descriptions
const logTypes = [
  {
    value: LogsTableName.FN_EDGE,
    label: 'Function Edge Logs',
    description: 'Edge function execution logs with request/response data',
  },
  {
    value: LogsTableName.AUTH,
