export function LogsManager({ projectRef }: { projectRef: string }) {
  const [activeTab, setActiveTab] = useState<LogsTableName>(LogsTableName.FN_EDGE)
  const [open, setOpen] = useState(false)

  const sql = useMemo(() => genDefaultQuery(activeTab), [activeTab])

  const { data: logs, isLoading, error } = useGetLogs(projectRef, { sql })

  const selectedLogType = logTypes.find((type) => type.value === activeTab)

  return (
    <>
      <div className="p-6 pt-4 lg:p-8 lg:pt-8 border-b bg-background z-10 sticky top-0 flex items-center justify-between mb-6">
        <div className="flex-1">
          <h1 className="text-base lg:text-xl font-semibold">Logs</h1>
          <p className="hidden lg:block text-sm lg:text-base text-muted-foreground mt-1">
            Debug errors and track activity in your app
          </p>
        </div>
        <Popover open={open} onOpenChange={setOpen}>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              role="combobox"
              aria-expanded={open}
              className="w-64 justify-between"
            >
              {selectedLogType ? selectedLogType.label : 'Select log type...'}
              <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-80 p-0" align="end">
            <Command>
              <CommandInput placeholder="Search log types..." className="h-9" />
              <CommandList
                className="max-h-60 overflow-y-auto"
                onWheel={(e) => {
                  e.stopPropagation()
                }}
                style={{ overscrollBehavior: 'contain' }}
              >
                <CommandEmpty>No log type found.</CommandEmpty>
                <CommandGroup>
                  {logTypes.map((logType) => (
                    <CommandItem
                      key={logType.value}
                      value={logType.value}
                      onSelect={(currentValue) => {
                        setActiveTab(currentValue as LogsTableName)
                        setOpen(false)
                      }}
                      className="flex items-center gap-2 p-3"
                    >
                      <div className="flex-1">
                        <div className="text-xs mb-1 font-medium leading-none">{logType.label}</div>
                        <div className="text-xs text-muted-foreground leading-snug">
                          {logType.description}
                        </div>
                      </div>
                      <Check
                        className={cn(
                          'h-4 w-4 mt-0.5',
                          activeTab === logType.value ? 'opacity-100' : 'opacity-0'
                        )}
                      />
                    </CommandItem>
                  ))}
                </CommandGroup>
              </CommandList>
            </Command>
          </PopoverContent>
        </Popover>
      </div>

      {isLoading && (
        <div className="space-y-2 mx-8 mt-8">
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
        </div>
      )}
      {(error || (logs && logs.error)) && (
        <div className="mx-6 lg:mx-8 mt-8">
          <Alert variant="destructive">
            <Terminal className="h-4 w-4" />
            <AlertTitle>Error fetching logs</AlertTitle>
            <AlertDescription>
              {(error as any)?.message ||
                (typeof logs?.error === 'object' && logs.error?.message) ||
                'An unexpected error occurred. Please try again.'}
            </AlertDescription>
          </Alert>
        </div>
      )}
      {logs && logs.result && logs.result.length > 0 && (
        <div className="mt-4 overflow-auto w-full">
          <Table>
            <TableHeader>
              <TableRow className="hover:bg-transparent">
                {Object.keys(logs.result[0] as object).map((key, idx, arr) => (
                  <TableHead
                    key={key}
                    className={
                      (idx === 0 ? 'first:pl-6 lg:first:pl-8 ' : '') +
                      (idx === arr.length - 1 ? 'last:pr-6 lg:last:pr-8 ' : '')
                    }
                  >
                    {key.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
                  </TableHead>
                ))}
              </TableRow>
            </TableHeader>
            <TableBody>
              {(logs.result as any[]).map((log, index) => (
                <TableRow key={log.id || index} className="group hover:bg-muted/50 relative">
                  {Object.keys(logs.result?.[0] ?? []).map((key, idx, arr) => {
                    const value = log[key]
                    const formattedValue = (() => {
                      if (key === 'timestamp' && typeof value === 'number') {
                        return new Date(value / 1000).toLocaleString()
                      }
                      if (value === null) {
                        return 'NULL'
                      }
                      return typeof value === 'object'
                        ? JSON.stringify(value, null, 2)
                        : String(value)
                    })()

                    return (
                      <TableCell
                        key={key}
                        className="first:pl-6 lg:first:pl-8 last:pr-6 lg:last:pr-8 text-xs text-muted-foreground group-hover:text-foreground min-w-[8rem]"
                      >
                        <HoverCard>
                          <HoverCardTrigger asChild>
                            <div className="text-xs font-mono w-fit max-w-96 truncate cursor-default">
                              {formattedValue}
                            </div>
                          </HoverCardTrigger>
                          <HoverCardContent className="w-96 max-h-96 overflow-auto p-3">
                            <pre className="text-xs font-mono whitespace-pre-wrap break-words">
                              {formattedValue}
                            </pre>
                          </HoverCardContent>
                        </HoverCard>
                      </TableCell>
                    )
                  })}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}
      {logs && logs.result && logs.result.length === 0 && (
        <div className="mt-8 mx-8">
          <Alert>
            <Logs className="h-4 w-4" />
            <AlertTitle>No logs found</AlertTitle>
            <AlertDescription>
              Logs will appear here when your application generates activity.
            </AlertDescription>
          </Alert>
        </div>
      )}
    </>
  )
}

'use client'

import { Button } from '@/registry/default/components/ui/button'
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/registry/default/components/ui/form'
import { Input } from '@/registry/default/components/ui/input'
import { Skeleton } from '@/registry/default/components/ui/skeleton'
import {
  useCreateSecrets,
  useDeleteSecrets,
  useGetSecrets,
} from '@/registry/default/platform/platform-kit-nextjs/hooks/use-secrets'
import { secretsSchema } from '../../lib/schemas/secrets'
import { zodResolver } from '@hookform/resolvers/zod'
import { AlertTriangle, Minus, PlusIcon, Key } from 'lucide-react'
import { useFieldArray, useForm } from 'react-hook-form'
import { z } from 'zod'
import { Alert, AlertDescription, AlertTitle } from '@/registry/default/components/ui/alert'

export function SecretsManager({ projectRef }: { projectRef: string }) {
  const { data: secrets, isLoading, error } = useGetSecrets(projectRef)
  const { mutate: createSecrets, isPending: isCreating } = useCreateSecrets()
  const { mutate: deleteSecrets, isPending: isDeleting } = useDeleteSecrets()
  const form = useForm<z.infer<typeof secretsSchema>>({
    resolver: zodResolver(secretsSchema),
    defaultValues: {
      secrets: [{ name: '', value: '' }],
    },
  })

  const { fields, append, remove } = useFieldArray({
    control: form.control,
    name: 'secrets',
  })

  const handleCreateSecrets = (formData: z.infer<typeof secretsSchema>) => {
    createSecrets(
      {
        projectRef,
        secrets: formData.secrets,
      },
      {
        onSuccess: () => {
          form.reset({ secrets: [{ name: '', value: '' }] })
        },
      }
    )
  }

  const handleDeleteSecret = (secretName: string) => {
    if (window.confirm(`Are you sure you want to delete the secret "${secretName}"?`)) {
      deleteSecrets({
        projectRef,
        secretNames: [secretName],
      })
    }
  }

  if (isLoading) {
    return (
      <div className="w-full max-w-4xl mx-auto p-12 space-y-4">
        <Skeleton className="h-48 w-full" />
        <Skeleton className="h-16 w-full" />
        <Skeleton className="h-16 w-full" />
      </div>
    )
  }

  if (error) {
    return (
      <div className="mx-6 lg:mx-8 mt-8">
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertTitle>Error loading secrets</AlertTitle>
          <AlertDescription>
            There was a problem loading your secrets configuration.
          </AlertDescription>
        </Alert>
      </div>
    )
  }

  return (
    <div className="w-full max-w-3xl mx-auto p-6 pt-4 lg:p-12 lg:pt-12">
      <div className="mb-16">
        <h2 className="text-base lg:text-xl font-semibold mb-1">Add New Secret</h2>
        <p className="text-muted-foreground mb-6 text-sm lg:text-base">
          Add sensitive information like API keys that your app needs to work securely.
        </p>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleCreateSecrets)} className="space-y-2">
            {fields.map((field, index) => (
              <div key={field.id} className="flex items-start space-x-2">
                <FormField
                  control={form.control}
                  name={`secrets.${index}.name`}
                  render={({ field }) => (
                    <FormItem className="flex-1">
                      {index === 0 && <FormLabel className="mb-4">Name</FormLabel>}
                      <FormControl>
                        <Input placeholder="SECRET_NAME" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name={`secrets.${index}.value`}
                  render={({ field }) => (
                    <FormItem className="flex-1">
                      {index === 0 && <FormLabel className="mb-4">Value</FormLabel>}
                      <FormControl>
                        <Input type="password" placeholder="••••••••" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="w-8">
                  {index === 0 && <FormLabel className="opacity-0 mb-4">Remove</FormLabel>}
                  <Button
                    type="button"
                    variant="outline"
                    size="icon"
                    onClick={() => remove(index)}
                    disabled={fields.length <= 1}
                  >
                    <Minus className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            ))}
            <div className="flex items-center space-x-2">
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={() => append({ name: '', value: '' })}
              >
                <PlusIcon className="mr-2 h-4 w-4" />
                Add Secret
              </Button>
            </div>

            <Button type="submit" disabled={isCreating} className="mt-6">
              {isCreating ? 'Creating...' : 'Create Secrets'}
            </Button>
          </form>
        </Form>
      </div>

      <div>
        <h2 className="font-semibold mb-4 lg:text-lg">Existing secrets</h2>
        {secrets && secrets.length > 0 ? (
          secrets.map((secret) => (
            <div
              key={secret.name}
              className="flex items-center justify-between py-4 border-b last:border-b-0"
            >
              <div>
                <p className="font-mono text-sm tracking-wider">{secret.name}</p>
                {secret.updated_at && (
                  <p className="text-xs text-muted-foreground mt-1">
                    Last updated: {new Date(secret.updated_at).toLocaleString()}
                  </p>
                )}
              </div>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleDeleteSecret(secret.name)}
                disabled={isDeleting}
              >
                Delete
              </Button>
            </div>
          ))
        ) : (
          <Alert>
            <Key className="h-4 w-4" />
            <AlertTitle>No secrets found</AlertTitle>
            <AlertDescription>No secrets found for this project.</AlertDescription>
          </Alert>
        )}
      </div>
    </div>
  )
}

'use client'

import { useGetSuggestions } from '@/registry/default/platform/platform-kit-nextjs/hooks/use-suggestions'
import { Alert, AlertDescription, AlertTitle } from '@/registry/default/components/ui/alert'
import { Terminal } from 'lucide-react'
import { Badge } from '@/registry/default/components/ui/badge'
import { useMemo } from 'react'

import ReactMarkdown from 'react-markdown'
import { Skeleton } from '@/registry/default/components/ui/skeleton'

export function SuggestionsManager({ projectRef }: { projectRef: string }) {
  const { data: suggestions, isLoading, error } = useGetSuggestions(projectRef)

  const sortedSuggestions = useMemo(() => {
    if (!suggestions) return []
    const levelOrder = { ERROR: 1, WARN: 2, INFO: 3 }
    return [...suggestions].sort((a: any, b: any) => {
      const levelA = levelOrder[a.level as keyof typeof levelOrder] || 99
      const levelB = levelOrder[b.level as keyof typeof levelOrder] || 99
      return levelA - levelB
    })
  }, [suggestions])

  const getBadgeVariant = (level: 'ERROR' | 'WARN' | 'INFO') => {
    switch (level) {
      case 'ERROR':
        return 'destructive'
      case 'WARN':
        return 'secondary'
      default:
        return 'outline'
    }
  }

  return (
    <div className="p-6 pt-4 lg:p-12 lg:pt-12 max-w-3xl mx-auto">
      <h2 className="text-base lg:text-xl font-semibold mb-1">Suggestions</h2>
      <p className="text-muted-foreground mb-4 lg:mb-8 text-sm lg:text-base">
        Improve your project's security and performance.
      </p>
      {isLoading && (
        <div className="space-y-2">
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
          <Skeleton className="h-6 w-full" />
        </div>
      )}
      {error && (
        <Alert variant="destructive" className="mt-8">
          <Terminal className="h-4 w-4" />
          <AlertTitle>Error fetching suggestions</AlertTitle>
          <AlertDescription>
            {(error as any)?.message || 'An unexpected error occurred. Please try again.'}
          </AlertDescription>
        </Alert>
      )}
      {suggestions && (
        <div>
          {sortedSuggestions.length > 0 ? (
            <div>
              {sortedSuggestions.map((suggestion: any) => (
                <div
                  key={suggestion.cache_key}
                  className="py-4 border-b last:border-b-0 group relative"
                >
                  <div className="flex-1">
                    <div className="flex justify-start items-start gap-4">
                      <h4 className="font-semibold text-sm">{suggestion.title}</h4>
                      <div className="flex items-center gap-1">
                        <Badge variant={getBadgeVariant(suggestion.level)} className="shrink-0">
                          {suggestion.level}
                        </Badge>
                        {suggestion.type && (
                          <Badge
                            variant={suggestion.type === 'security' ? 'destructive' : 'secondary'}
                            className="shrink-0"
                          >
                            {suggestion.type.charAt(0).toUpperCase() + suggestion.type.slice(1)}
                          </Badge>
                        )}
                      </div>
                    </div>
                    <div className="text-sm text-muted-foreground mt-2 prose prose-sm max-w-none">
                      <ReactMarkdown
                        components={{
                          code({ inline, children, ...props }: any) {
                            return inline ? (
                              <code className="bg-muted px-1 rounded" {...props}>
                                {children}
                              </code>
                            ) : (
                              <pre className="bg-muted p-2 rounded overflow-x-auto" {...props}>
                                <code>{children}</code>
                              </pre>
                            )
                          },
                        }}
                      >
                        {suggestion.detail}
                      </ReactMarkdown>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <Alert>
              <Terminal className="h-4 w-4" />
              <AlertTitle>No suggestions found</AlertTitle>
              <AlertDescription>
                Your project looks good! No suggestions at this time.
              </AlertDescription>
            </Alert>
          )}
        </div>
      )}
    </div>
  )
}

