'use client'

import { useQuery } from '@tanstack/react-query'
import { runQuery } from '@/registry/default/platform/platform-kit-nextjs/hooks/use-run-query'

// GET User Counts by day
const getUserCountsByDay = ({ projectRef, days }: { projectRef: string; days: number }) => {
  const sql = `
    WITH days_series AS (
      SELECT generate_series(
        date_trunc('day', now() - interval '${Number(days) - 1} days'),
        date_trunc('day', now()),
        '1 day'::interval
      )::date AS date
    )
    SELECT
      d.date,
      COALESCE(u.users, 0)::int as users
    FROM
      days_series d
    LEFT JOIN (
      SELECT
        date_trunc('day', created_at AT TIME ZONE 'UTC')::date as date,
        count(id) as users
      FROM
        auth.users
      GROUP BY 1
    ) u ON d.date = u.date
    ORDER BY
      d.date ASC;
  `

  return runQuery({
    projectRef,
    query: sql,
    readOnly: true,
  })
}

export const useGetUserCountsByDay = (projectRef: string, days: number) => {
  return useQuery({
    queryKey: ['user-counts', projectRef, days],
    queryFn: () => getUserCountsByDay({ projectRef, days }),
    enabled: !!projectRef,
    retry: false,
  })
}

export enum LogsTableName {
  FN_EDGE = 'function_edge_logs',
  AUTH = 'auth_logs',
  POSTGRES = 'postgres_logs',
  REALTIME = 'realtime_logs',
  STORAGE = 'storage_logs',
  PG_CRON = 'pg_cron_logs',
  EDGE = 'edge_logs',
  FUNCTIONS = 'function_logs',
  POSTGREST = 'postgrest_logs',
  SUPAVISOR = 'supavisor_logs',
  PGBOUNCER = 'pgbouncer_logs',
  WAREHOUSE = 'warehouse_logs',
  PG_UPGRADE = 'pg_upgrade_logs',
}

const genCrossJoinUnnests = (table: LogsTableName) => {
  switch (table) {
    case LogsTableName.EDGE:
      return `cross join unnest(metadata) as m
  cross join unnest(m.request) as request
  cross join unnest(m.response) as response`
    case LogsTableName.POSTGRES:
      return `cross join unnest(metadata) as m
  cross join unnest(m.parsed) as parsed`
    case LogsTableName.FUNCTIONS:
      return `cross join unnest(metadata) as metadata`
    case LogsTableName.AUTH:
      return `cross join unnest(metadata) as metadata`
    case LogsTableName.FN_EDGE:
      return `cross join unnest(metadata) as m
  cross join unnest(m.response) as response
  cross join unnest(m.request) as request`
    case LogsTableName.SUPAVISOR:
      return `cross join unnest(metadata) as m`
    default:
      return ''
  }
}

export const genDefaultQuery = (table: LogsTableName, limit: number = 100) => {
  const joins = genCrossJoinUnnests(table)
  const orderBy = 'order by timestamp desc'

  switch (table) {
    case LogsTableName.EDGE:
      return `select id, ${table}.timestamp, event_message, request.method, request.path, request.search, response.status_code
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.POSTGRES:
      return `select ${table}.timestamp, id, event_message, parsed.error_severity, parsed.detail, parsed.hint
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.FUNCTIONS:
      return `select id, ${table}.timestamp, event_message, metadata.event_type, metadata.function_id, metadata.level
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.AUTH:
      return `select id, ${table}.timestamp, event_message, metadata.level, metadata.status, metadata.path, metadata.msg as msg, metadata.error
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.FN_EDGE:
      return `select id, ${table}.timestamp, event_message, response.status_code, request.method, m.function_id, m.execution_time_ms, m.deployment_id, m.version
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.SUPAVISOR:
      return `select id, ${table}.timestamp, event_message
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.PG_UPGRADE:
      return `select id, ${table}.timestamp, event_message
from ${table}
${joins}
${orderBy}
limit ${limit}`

    case LogsTableName.PG_CRON:
      return `select postgres_logs.timestamp, id, event_message, parsed.error_severity, parsed.query
from postgres_logs
cross join unnest(metadata) as m
cross join unnest(m.parsed) as parsed
where (parsed.application_name = 'pg_cron' OR event_message LIKE '%cron job%')
${orderBy}
limit ${limit}`

    default:
      return `select id, ${table}.timestamp, event_message
from ${table}
${joins}
${orderBy}
limit ${limit}`
  }
}

import createClient from 'openapi-fetch'
import type { paths } from './management-api-schema'

export const client = createClient<paths>({
  baseUrl: '/api/supabase-proxy',
})

import { stripIndent } from 'common-tags'

import { columnsSql, extensionsSql, tablesSql } from './sql'

export const SYSTEM_SCHEMAS = [
  'information_schema',
  'pg_catalog',
  'pg_toast',
  '_timescaledb_internal',
]

/**
 * Generates the SQL query to list tables in the database.
 */
export function listTablesSql(schemas: string[] = []) {
  let sql = stripIndent`
    with
      tables as (${tablesSql}),
      columns as (${columnsSql})
    select
      *,
      ${coalesceRowsToArray('columns', 'columns.table_id = tables.id')}
    from tables
  `

  sql += '\n'

  if (schemas.length > 0) {
    sql += `where schema in (${schemas.map((s) => `'${s}'`).join(',')})`
  } else {
    sql += `where schema not in (${SYSTEM_SCHEMAS.map((s) => `'${s}'`).join(',')})`
  }

  return sql
}

/**
 * Generates the SQL query to list all extensions in the database.
 */
export function listExtensionsSql() {
  return extensionsSql
}

/**
 * Generates a SQL segment that coalesces rows into an array of JSON objects.
 */
export const coalesceRowsToArray = (source: string, filter: string) => {
  return stripIndent`
    COALESCE(
      (
        SELECT
          array_agg(row_to_json(${source})) FILTER (WHERE ${filter})
        FROM
          ${source}
      ),
      '{}'
    ) AS ${source}
  `
}

import z from 'zod'

export const secretsSchema = z
  .object({
    secrets: z.array(
      z.object({
        name: z
          .string()
          .min(1, 'Secret name is required.')
          .regex(
            /^[a-zA-Z_][a-zA-Z0-9_]*$/,
            'Must contain letters, numbers, and underscores, starting with a letter or underscore.'
          ),
        value: z.string().min(1, 'Secret value is required.'),
      })
    ),
  })
  .describe('Secrets schema for managing environment variables.')

export type SecretsSchema = z.infer<typeof secretsSchema>

export const deleteSecretsSchema = z
  .object({
    secretNames: z
      .array(z.string().min(1, 'Secret name cannot be empty.'))
      .min(1, 'At least one secret name is required.'),
  })
  .describe('Schema for deleting secrets by name.')
export type DeleteSecretsSchema = z.infer<typeof deleteSecretsSchema>

'use client'

import { createContext, useContext, useState, ReactNode, useMemo, useCallback } from 'react'

type StackItem = {
  title: string
  component: ReactNode
}

type SheetNavigationContextType = {
  stack: StackItem[]
  push: (item: StackItem) => void
  pop: () => void
  popTo: (index: number) => void
  reset: () => void
}

const SheetNavigationContext = createContext<SheetNavigationContextType | undefined>(undefined)

export function SheetNavigationProvider({
  children,
  onStackEmpty,
  initialStack = [],
}: {
  children: ReactNode
  onStackEmpty?: () => void
  initialStack?: StackItem[]
}) {
  const [stack, setStack] = useState<StackItem[]>(initialStack)

  const push = useCallback((item: StackItem) => {
    setStack((prev) => [...prev, item])
  }, [])

  const pop = useCallback(() => {
    setStack((prev) => {
      const newStack = prev.slice(0, -1)
      if (newStack.length === 0) {
        onStackEmpty?.()
      }
      return newStack
    })
  }, [onStackEmpty])

  const popTo = useCallback((index: number) => {
    if (index < 0) return
    setStack((prev) => prev.slice(0, index + 1))
  }, [])

  const reset = useCallback(() => {
    setStack([])
    onStackEmpty?.()
  }, [onStackEmpty])

  const value = useMemo(
    () => ({ stack, push, pop, popTo, reset }),
    [stack, push, pop, popTo, reset]
  )

  return <SheetNavigationContext.Provider value={value}>{children}</SheetNavigationContext.Provider>
}

export function useSheetNavigation() {
  const context = useContext(SheetNavigationContext)
  if (context === undefined) {
    throw new Error('useSheetNavigation must be used within a SheetNavigationProvider')
  }
  return context
}
