const template = `
---
id: usage
title: Usage
---

<!-- AUTOGENERATED: DO NOT EDIT DIRECTLY IF THIS IS VERSION "next" -->

<%- info.description %>

<% commands.forEach(function(command){ %>

<!-- AUTOGENERATED: DO NOT EDIT DIRECTLY IF THIS IS VERSION "next" -->

## <%= command.summary %>

<%- command.description %>

### Usage

<%- command.usage %>

<% if(command.subcommands.length > 0){ %>
### Available Commands

<% command.subcommands.forEach(function(subcommand){ %>
- <%- subcommand %>
<% }); %>
<% } %>

### Options

<%- command.options %>

<% }); %>
`.trim()

export default template
